custom_css:
/* change the background color of the form */
.web-form {
    background-color: #F9F9F9;
    border-radius: 10px;
    padding: 20px;
}

/* change the color and font-size of field labels */
.form-group label {
    color: #333;
    font-size: 1.2em;
}

/* add some padding to the fields */
.form-group input, .form-group select, .form-group textarea {
    padding: 10px;
}

/* style the submit button */
.web-form-footer .btn-primary {
    background-color: #008080;
    border-color: #008080;
}

/* change the color of the submit button on hover */
.web-form-footer .btn-primary:hover {
    background-color: #006666;
    border-color: #006666;
}
